    private void button_se_connecterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button_se_connecterActionPerformed

        Administrateur admin = new Administrateur();

        admin.setEmail(text_field_email.getText());
        admin.setMotDePasse(password_field.getText());

        if (new AdministrateurAPI().login(admin)) {
            MainMenuJFrame menu = new MainMenuJFrame();
            menu.setVisible(true);
            this.dispose();
        } else {
            JOptionPane.
                    showMessageDialog(null, "email ou mot de passe erron√©e");
        }

    }//GEN-LAST:event_button_se_connecterActionPerformed

